package top.arron206.whisper.service.impl;

import org.apache.shiro.SecurityUtils;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;
import top.arron206.whisper.dao.UserRepository;
import top.arron206.whisper.entity.User;
import top.arron206.whisper.service.UserService;
import java.util.Random;

@Service
public class UserServiceImpl implements UserService {
    private User user;
    private User userInDataBase;
    static String pic1 = "data:image/jpeg;base64,";
    static String pic2 = "data:image/jpeg;base64,";
    static String pic3 = "data:image/jpeg;base64,";
    static String pic4 = "data:image/jpeg;base64,";
    static String pic5 = "data:image/jpeg;base64,";
    private static String[] pics = new String[]{pic1, pic2, pic3, pic4, pic5};

    @Autowired
    private UserRepository userRepository;

    @Override
    public User getUser() {
        return user;
    }

    @Override
    public void setUser(User user) {
        this.user = user;
        this.userInDataBase = userRepository.findByAccount(user.getAccount());
    }

    @Override
    public User setUser(){
        org.apache.shiro.subject.Subject subject = SecurityUtils.getSubject();
        Integer id = (Integer) subject.getPrincipal();
        this.user = findById(id);
        return this.user;
    }

    @Override
    public User getUserInDataBase() {
        return userInDataBase;
    }

    @Override
    public void setUserInDataBase(User userInDataBase) {
        this.userInDataBase = userInDataBase;
    }

    @Override
    public void encode(){
        this.user.md5Password();
    }

    @Override
    public boolean checkPassword(){
        return this.user.getPassword().equals(this.userInDataBase.getPassword());
    }

    @Override
    public void initPic(){
        Random random = new Random();
        this.user.setPic(pics[random.nextInt(5)]);
    }

    @Override
    public void setGender(String gender){
        this.user.setGender(gender);
    }

    @Override
    public void setAge(int age){
        this.user.setAge(age);
    }

    @Override
    public void save() {
        userRepository.save(this.user);
    }

    @Override
    public boolean isRegistered() {
       return  userInDataBase!=null;
    }

    @Override
    public User findById(int id){
        return userRepository.findById(id);
    }


}
